import React, { Component } from 'react'
import './PokemonList.css'
import '../../App.css';
import logo from '../../logo.svg'
import Card from '@material-ui/core/Card';
import GridList from '@material-ui/core/GridList';
import GridListTile from '@material-ui/core/GridListTile';
import GridListTileBar from '@material-ui/core/GridListTileBar';
import IconButton from '@material-ui/core/IconButton';
import InfoIcon from '@material-ui/icons/Info';
import pokemonListData from '../../Component/PokemonList/PokemonListData.js';
import Pagination from '@material-ui/lab/Pagination';
import InputLabel from '@material-ui/core/InputLabel';
import MenuItem from '@material-ui/core/MenuItem';
import FormControl from '@material-ui/core/FormControl';
import Select from '@material-ui/core/Select';
import Chip from '@material-ui/core/Chip';
import Avatar from '@material-ui/core/Avatar';
import DoneIcon from '@material-ui/icons/Done';
import Fab from '@material-ui/core/Fab';
import Grow from '@material-ui/core/Grow';

/**
* @author
* @function PokemonList
**/

class PokemonList extends Component{

  constructor(props) {
    super(props);
    this.state = {
      error: null,
      pokemons: []
    };
  }

    componentDidMount() {
      const gqlQuery = `query pokemons($limit: Int, $offset: Int) {
        pokemons(limit: $limit, offset: $offset) {
          count
          next
          previous
          status
          message
          results {
            url
            name
            image
          }
        }
      }`;
      const gqlVariables = {
        limit: 2,
        offset: 1,
      };

        fetch("https://graphql-pokeapi.vercel.app/api/graphql",{
        credentials: 'omit',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({
          query: gqlQuery,
          variables: gqlVariables,
        }),
        method: 'POST',
      })
        .then(res => res.json())
        .then((result) => {
          this.setState({ pokemons: result.data.pokemons.result })
          console.log(this.state.pokemons);
        })
        .catch(console.log)
      }

      render() {
        // const { pokemons } = this.state;
      //   var audienses = this.state.pokemons.map((tile,index) => (
      //     <Grow in='true'>
      //                     <GridListTile className='gridTile' key={tile.images}>
      //                       <img src={tile.images} alt={tile.name} />
      //                       <GridListTileBar
      //                         className='gridListTileBar'
      //                         title={tile.name} 
      //                         subtitle={[
      //                           <Fab variant="extended" className='fab'>
      //                             Grass
      //                           </Fab>
      //                         ]}
      //                         actionIcon={
      //                           <IconButton aria-label={`info about adasdasdasda`} className='icon'>
      //                             <InfoIcon />
      //                           </IconButton>
      //                         }
      //                       />
      //                   </GridListTile>
      //                   </Grow>
      //  ));
        return(
              <Card className='card'>
                  <div className='header'>
                      List Pokemon
                  </div>
                  <div className='search'>
                    Ini buat search
                  </div>
                  <div className='list'>
                    <Card className='card-value'>
                    <GridList cols={4} cellHeight={180} className='gridList'>
                      <GridListTile cols={4} style={{ height: 'auto' }}>
                      </GridListTile>
                        {/* {audienses} */}
                     </GridList>
                     <div className='pagination'>
                       <Pagination count={11} defaultPage={6} boundaryCount={2} />
                     </div>
                     </Card>
                 </div>
              </Card>
             )

      }
}

export default PokemonList;
