import React, { Component } from 'react'
import './PokemonList.css'
import '../../App.css';
import logo from '../../logo.svg'
import Card from '@material-ui/core/Card';
import GridList from '@material-ui/core/GridList';
import GridListTile from '@material-ui/core/GridListTile';
import GridListTileBar from '@material-ui/core/GridListTileBar';
import IconButton from '@material-ui/core/IconButton';
import InfoIcon from '@material-ui/icons/Info';
import pokemonListData from '../../Component/PokemonList/PokemonListData.js';
import Pagination from '@material-ui/lab/Pagination';
import InputLabel from '@material-ui/core/InputLabel';
import MenuItem from '@material-ui/core/MenuItem';
import FormControl from '@material-ui/core/FormControl';
import Select from '@material-ui/core/Select';
import Chip from '@material-ui/core/Chip';
import Avatar from '@material-ui/core/Avatar';
import DoneIcon from '@material-ui/icons/Done';
import Fab from '@material-ui/core/Fab';
import Grow from '@material-ui/core/Grow';

/**
* @author
* @function PokemonList
**/

class PokemonList extends Component{

  constructor(props) {
    super(props);
    this.state = {
      error: null,
      pokemons: [],
      type: [],
      grow: false,
      next :'',
      prev :'',
      count:''
    };
  }

    componentDidMount() {
      const gqlQueryPokemon = `query pokemons($limit: Int, $offset: Int) {
        pokemons(limit: $limit, offset: $offset) {
          count
          next
          previous
          status
          message
          results {
            url
            name
            image
          }
        }
      }`;
      const gqlVariables = {
        limit: 20,
        offset: 0,
      };

        fetch("https://graphql-pokeapi.vercel.app/api/graphql",{
        credentials: 'omit',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({
          query: gqlQueryPokemon,
          variables: gqlVariables,

        }),
        method: 'POST',
      })
        .then(res => res.json())
        .then((result) => {
          this.setState({ 
            pokemons: result.data.pokemons.results, 
            grow:true,
            next :result.data.pokemons.next,
            prev :result.data.pokemons.previous,
            count:result.data.pokemons.count })
          console.log(result)
        })
        .catch(console.log)
      }

      getType(url){
          fetch(url)
          .then(res => res.json())
          .then((result) => {
            this.setState({ type: result, grow:true })
            console.log(result)
          })
          .catch(console.log)

      }

      render() {
        const {next,prev,count}=this.state
        var audienses = this.state.pokemons.map((tile,index) => (
          <Grow in={this.state.grow}>
                          <GridListTile className='gridTile' key={index} >
                            <img src={tile.image} alt={tile.name} />
                            <GridListTileBar
                              className='gridListTileBar'
                              title={tile.name} 
                              subtitle={[
                                <Fab variant="extended" className='fab'>
                                  {tile.url}
                                </Fab>
                              ]}
                              actionIcon={
                                <IconButton aria-label={`info about adasdasdasda`} className='icon'>
                                  <InfoIcon />
                                </IconButton>
                              }
                            />
                        </GridListTile>
                        </Grow>
       ));
        return(
              <Card className='card'>
                  <div className='header'>
                      List Pokemon
                  </div>
                  <div className='search'>
                    Ini buat search
                  </div>
                  <div className='list'>
                    <Card className='card-value'>
                    <GridList cols={4} cellHeight={180} className='gridList'>
                      <GridListTile cols={4} style={{ height: 'auto' }}>
                      </GridListTile>
                        {audienses}
                     </GridList>
                     <div className='pagination'>
                       <Pagination count={count%20} defaultPage={1} boundaryCount={2} />
                     </div>
                     </Card>
                 </div>
              </Card>
             )

      }
}

export default PokemonList;
