import React, { Component } from 'react'
import './DetailPokemon.css';
import '../../App.css';
import { Card } from '@material-ui/core';
import GridList from '@material-ui/core/GridList';
import GridListTile from '@material-ui/core/GridListTile';
import Grow from '@material-ui/core/Grow';

/**
* @author
* @function DetailPokemon
**/

class DetailPokemon extends Component{

  constructor(props) {
    super(props);
    this.state = {
      error: null,
      pokemon: [],
      grow: false,

    };
  }

    componentDidMount() {
      console.log(this.props)
      fetch(this.props.url)
        .then(res => res.json())
        .then((result) => {
          this.setState({ 
            pokemon: result, 
            grow:true})
        })
        .catch(console.log)
      }



      render() {
        const {pokemon}=this.state
        const image="https://raw.githubusercontent.com/PokeAPI/sprites/master/sprites/pokemon/other/official-artwork/"+pokemon.id+".png"
        console.log(image)
        return(
          <Card className='card'>
            {/* <Card className='pokedex-pokemon-profile'> */}
            <GridList cols={2} cellHeight={180} className='gridListDetail'>
              <GridListTile cols={2} >
                </GridListTile>
            <Grow in={this.state.grow}>
              <GridListTile className='gridTileDetail' key={pokemon.id} >
              <img src={image} alt={pokemon.name}  className='imagesDetail'/>
              </GridListTile>
            </Grow>
            </GridList>
            </Card>
          //  </Card>
        )
      }
 }

export default DetailPokemon;
